(55, 1, '2015-04-09 10:29:08', '2015-04-09 10:29:08', '', 'trai-nghiem-cong-nghe-ao-nhu-phim-ma-tran-tai-hoi-thao-facebook2', '', 'inherit', 'open', 'open', '', 'trai-nghiem-cong-nghe-ao-nhu-phim-ma-tran-tai-hoi-thao-facebook2', '', '', '2015-04-09 10:29:08', '2015-04-09 10:29:08', '', 37, 'http://localhost/thucteao/wp-content/uploads/2015/04/trai-nghiem-cong-nghe-ao-nhu-phim-ma-tran-tai-hoi-thao-facebook2.jpg', 0, 'attachment', 'image/jpeg', 0),
